import {SettingToggle,TextStyle,Card,Button,Form,FormLayout,Layout,Page,Stack,TextField} from '@shopify/polaris';

class AnnotatedLayout extends React.Component {
    state = {
        discount:'10%',
        enabled: false
    };
    render() {
        const {discount,enabled} = this.state;
        const contentStatus = enabled? 'Disable': 'Enable';
        const textStatus = enabled? 'enabled':'disabled';
  
        return (
            <Page>
                <Layout>
                    <Layout.AnnotatedSection
                    title="Default discount"
                    description="Add a product to Sample App, it will be automatically discounted"
                    >
                    <Card sectioned>
                        <Form onSubmit={this.handleSubmit}>
                            <FormLayout>
                                <TextField
                                value={discount}
                                onChange={this.handleChange('discount')}
                                label="Discount percentage"
                                type="discount"/>
                            <Stack distribution="trailing">
                                <Button primary submit>
                                    Save
                                </Button>
                            </Stack>    
                            </FormLayout>
                        </Form>
                    </Card>
                    </Layout.AnnotatedSection>
                    <Layout.AnnotatedSection
                    title="Price updates"
                    description="Temporarly disable all Sample App price updates"
                    >
                        <SettingToggle
                        action={
                            {
                                content:contentStatus,
                                onAction: this.handleToggle,
                            }

                        }
                        enabled={enabled}>
                            This setting is {' '}
                    <TextStyle variation="strong">{textStatus}</TextStyle>
                    </SettingToggle>
                    </Layout.AnnotatedSection>
                </Layout>
            </Page>
        );

    }
    handleSubmit= ()=>{
        this.setState({
            discount:this.state.discount,
        });
        console.log('submission',this.state);
    };
    handleChange = (field) =>{
        return (value)=> this.setState({[field]:value});
    };
}

export default AnnotatedLayout;
